//   { value: '(GMT-03:30) Newfoundland', label: '(GMT-03:30) Newfoundland' }
// ]

const AccountTabs = ({ }) => {
  // ** Hooks
  const [sidebarOpen, setSidebarOpen] = useState(false)
  const toggleSidebar = () => setSidebarOpen(!sidebarOpen)

  return (
    <Fragment>
      <Row>
      <Col lg='3' sm='6'>
          <StatsHorizontal
            color='danger'
            onClick={toggleSidebar} 
            statTitle='Edit your account info'
            icon={<Edit size={20} />}
            renderStats={<h3 className='fw-bolder mb-75'>Account Info</h3>}
          />
        </Col>
        <Col lg='3' sm='6'>
          <StatsHorizontal
            color='warning'
            statTitle='Change your sector'
            icon={<List size={20} onClick={toggleSidebar} />}
            renderStats={<h3 className='fw-bolder mb-75'>Change Sector</h3>}
          />
           <Col className='mb-1' md='6' sm='12'>
            <Label className='form-label'>Icons</Label>
            <Select
              options={iconOptions}
              className='react-select'
              classNamePrefix='select'
              components={{
                Option: OptionComponent
              }}
            />
          </Col>
        </Col>

          {/* <Card>
          <CardHeader className='border-bottom'>
            <CardTitle tag='h4'>Account Info</CardTitle>
          </CardHeader>
          <CardBody className='py-2 my-25'>

          </CardBody>
        </Card> */}

      </Row>
      <Sidebar open={sidebarOpen} toggleSidebar={toggleSidebar} />
      <DeleteAccount />
    </Fragment>
  )
}

export default AccountTabs
