// ** Third Party Components
import Nouislider from 'nouislider-react'
import { useState } from 'react'

// ** Reactstrap Imports
import { Card, CardHeader, CardTitle, CardBody } from 'reactstrap'

const SliderScalePips = ({ direction }) => {
  const { textValue, percent } = useState['']
  return (
    <Card>
      <CardHeader>
        <CardTitle tag='h4'>Slider Scales / Pips</CardTitle>
      </CardHeader>
      <CardBody>
        <Nouislider
          step={10}
          start={10}
          tooltips={true}
          className='mt-1 mb-3'
          direction={direction}
          range={{
            min: 0,
            max: 100
          }}
          pips={{
            mode: 'steps',
            stepped: true,
            density: 5
          }}
        />
        {textValue && percent && (
          <div>
            Value: {textValue}, {percent} %
          </div>
        )}
      </CardBody>
    </Card>
  )
}

export default SliderScalePips
