import React, { useContext, createContext } from 'react';
import { RefTracker } from '../hooks';

const Context = createContext({} as RefTracker);

/**
 * Access parent ref tracker.
 */
export function useRefTrackerContext() {
  return useContext(Context);
}

export interface RefTrackerProviderProps {
  refTracker: RefTracker;
}

export const RefTrackerProvider: React.FC<RefTrackerProviderProps> = props => {
  return (
    <Context.Provider value={props.refTracker}>
      {props.children}
    </Context.Provider>
  );
};
import { EVENT } from '../constants';

export type EventType = string | number | symbol;
export type Handler<T = any> = (args: T) => void;

export interface EventManager<E = EventType> {
  on<T = any>(event: E, handler: Handler<T>): EventManager<E>;
  off<T = any>(event: E, handler?: Handler<T>): EventManager<E>;
  emit<T = any>(event: E, args?: T): EventManager<E>;
}

function createEventManager<E = EventType>(): EventManager<E> {
  const eventList = new Map<E, Set<Handler>>();

  return {
    on<T = any>(event: E, handler: Handler<T>) {
      //eslint-disable-next-line @typescript-eslint/no-unused-expressions
      eventList.has(event)
        ? eventList.get(event)?.add(handler)
        : eventList.set(event, new Set([handler]));
      return this;
    },
    off<T = any>(event: E, handler?: Handler<T>) {
      handler ? eventList.get(event)!.delete(handler) : eventList.delete(event);
      return this;
    },
    emit<T = any>(event: E, args: T) {
      if (process.env.NODE !== 'production') {
        const currentEv = (event as unknown) as number;

        if (!eventList.has(event) && currentEv !== EVENT.HIDE_ALL) {
          console.error(
            `It seems that the menu you are trying to display is not renderer or you have a menu id mismatch.`,
            `You used the menu id: ${event}`
          );
        }
      }
      eventList.has(event) &&
        eventList.get(event)!.forEach((handler: Handler<T>) => {
          handler(args);
        });
      return this;
    },
  };
}

export const eventManager = createEventManager();
import { useEffect, useRef } from 'react';

export function usePrevious<T>(value: T) {
  const ref = useRef<T>();

  useEffect(() => {
    ref.current = value;
  }, [value]);

  return ref.current;
}
import { useRef } from 'react';

export interface RefTrackerValue {
  node: HTMLElement;
  isSubmenu: boolean;
  submenuRefTracker?: RefTracker;
}

export type RefTracker = ReturnType<typeof useRefTracker>;

/**
 * Used to store children refs
 */
export function useRefTracker() {
  return useRef<Map<HTMLElement, RefTrackerValue>>(new Map()).current;
}
import { eventManager } from './eventManager';
import { MenuId, TriggerEvent } from '../types';
import { SyntheticEvent } from 'react';

import { EVENT } from '../constants';

export interface ShowContextMenuParams {
  id: MenuId;
  event: TriggerEvent;
  props?: any;
  position?: {
    x: number;
    y: number;
  } | null;
}

export interface ContextMenu {
  show: (params: ShowContextMenuParams) => void;
  hideAll: () => void;
}

const contextMenu: ContextMenu = {
  show({ id, event, props, position }) {
    if (event.preventDefault) event.preventDefault();

    eventManager.emit(EVENT.HIDE_ALL).emit(id, {
      event: (event as SyntheticEvent).nativeEvent || event,
      props,
      position,
    });
  },
  hideAll() {
    eventManager.emit(EVENT.HIDE_ALL);
