// ** React Import
import { useState } from 'react'

// ** Custom Components
import Sidebar from '@components/sidebar'
import 'cleave.js/dist/addons/cleave-phone.us'
import { EditorState } from 'draft-js'
import { Editor } from 'react-draft-wysiwyg'
import { SlideDown } from 'react-slidedown'
import Repeater from '@components/repeater'
// ** Utils
  // import StaffAgrementEditor from '../'
// ** Reactstrap Imports
import { Card, CardBody, Alert, Button, Label, FormText, Input, UncontrolledTooltip, } from 'reactstrap'
import { Info, Plus } from 'react-feather'
// ** Store & Actions
// import { addUser } from '../store'
// import { useDispatch } from 'react-redux'
import '@styles/react/libs/editor/editor.scss'
const SidebarPayBand = ({ open, toggleSidebarPayBand }) => {
  // ** States
  const [count, setCount] = useState(1)

  const increaseCount = () => {
    setCount(count + 1)
  }

  const deleteForm = e => {
    e.preventDefault()
    const slideDownWrapper = e.target.closest('.react-slidedown'),
      form = e.target.closest('form')
    if (slideDownWrapper) {
      slideDownWrapper.remove()
    } else {
      form.remove()
    }
  }
  const handleSidebarClosedStaffTerms = () => {

  }
  const [value, setValue] = useState(EditorState.createEmpty())
  return (

    <Sidebar
      size='lg'
      open={open}
      style={{maxWidth:'90%', width:'45em'}}
      title='Edit Staff Pay Band'
      headerClassName='mb-1'
      contentClassName='pt-0'

      toggleSidebarPayBand={toggleSidebarPayBand}
      onClosed={handleSidebarClosedStaffTerms}
    > 
            <Repeater count={count}>
          {i => {
            const Tag = i === 0 ? 'div' : SlideDown
            return (
              <Tag key={i}>
                <Form>
                  <Row className='justify-content-between align-items-center'>
                    <Col md={8} className='mb-md-0 mb-1'>
                      <Label className='form-label' for={`animation-item-name-${i}`}>
                        Band Name
                      </Label>
                      <Input type='text' id={`animation-item-name-${i}`} placeholder='eg: Yeovil' />
                    </Col>

                    <Col md={2}>
                      <Button color='danger' className='text-nowrap px-1 mt-2' size='sm' onClick={deleteForm} outline>
                        <X size={14} className='me-50' />
                        <span>Delete</span>
                      </Button>
                    </Col>

                    <Col sm={12}>
                      <hr />
                    </Col>
                  </Row>
                </Form>
              </Tag>
            )
          }}
        </Repeater>
        <Button className='btn-icon' color='primary'  size='sm' onClick={increaseCount}>
          <Plus size={14} />
          <span className='align-middle ms-25'>Add New</span>
        </Button>
          <Alert color='primary'>
            <div className='alert-body'>
              <Info size={18}   id='UnControlledExample'/>
              <span className='ms-1'>The software has two types of staff pay rates, Common Rate and Different Rate. In the Common Rate, all staff members who work for a client (per staff type, for example, all HCAs who work for a client) will be paid the same rate. When it comes to Different Rates, you can create Pay Bands and assign them to staff members. You can create unlimited Pay Bands, for example RGN Band 5, Senior HCA etc).
              </span>
            </div>
          </Alert>
          <Alert color='warning'>
            <div className='alert-body'>
              <Info size={18}   id='UnControlledExample'/>
              <span className='ms-1'>This is a 2 Step process. First 'Add a New Band', then select if from the drop down menu, set the days, enter rates and save it. You can edit the previously created ones or delete them from the bottom dropdown.
              </span>
            </div>
          </Alert>
        <hr/>
          <div className='mb-2'>
          <Label className='form-label' for='user-role'>
            Select Terms and Conditions type
          </Label>
          <Input type='select' id='user-role' name='user-type-agrement'>
            <option value='Staff'>Staff</option>
            <option value='Client'>Client</option>
          </Input>
        </div>
          <Editor editorState={value} onEditorStateChange={data => setValue(data)} />
          <hr/>
          <Button type='submit' className='me-1 ' color='primary'>
          Submit
        </Button>
        <Button type='reset' color='secondary' outline onClick={toggleSidebarPayBand}>
          Cancel
        </Button>


    </Sidebar>
  )
}

export default SidebarPayBand
