// ** React Imports
import { useEffect, useState } from 'react'

// ** Third Party Components
import axios from 'axios'
import { UserCheck } from 'react-feather'

// ** Custom Components
import StatsWithLineChart from '@components/widgets/stats/StatsWithLineChart'

const ActiveUsers = ({ success }) => {
  // ** State
  const [data, setData] = useState(null)

  useEffect(() => {
    axios.get('/card/card-statistics/active-users').then(res => setData(res.data))
  }, [])

  const options = {
    chart: {
      id: 'activeUsers',
      toolbar: {
        show: false
      },
      sparkline: {
        enabled: true
      },
      dropShadow: {
        enabled: true,
        top: 5,
        left: 0,
        blur: 4,
        opacity: 0.1
      }
    },
    grid: {
      show: false
    },
    colors: [success],
    dataLabels: {
      enabled: false
    },
    stroke: {
      curve: 'smooth',
      width: 5
    },
    fill: {
      type: 'gradient',
      gradient: {
        shadeIntensity: 1,
        gradientToColors: ['#55DD92'],
        opacityFrom: 1,
        opacityTo: 1,
        stops: [0, 100, 100, 100]
      }
    },

    xaxis: {
      labels: {
        show: false
      },
      axisBorder: {
        show: false
      }
    },
    yaxis: {
      labels: {
        show: false
      }
    },
    tooltip: {
      x: { show: false }
    }
  }

  return data !== null ? (
    <StatsWithLineChart
      icon={<UserCheck size={21} />}
      color='success'
      stats='659.8k'
      statTitle='Active Users'
      series={data.series}
      options={options}
      type='line'
    />
  ) : null
}

export default ActiveUsers
