// ** React Import
import { useState } from 'react'

// ** Custom Components
import Sidebar from '@components/sidebar'
import 'cleave.js/dist/addons/cleave-phone.us'
import { EditorState } from 'draft-js'
import { Editor } from 'react-draft-wysiwyg'
// ** Utils
  // import StaffAgrementEditor from '../'
// ** Reactstrap Imports
import { Card, CardBody, Alert, Button, Label, FormText, Input} from 'reactstrap'
import { Info } from 'react-feather'
// ** Store & Actions
// import { addUser } from '../store'
// import { useDispatch } from 'react-redux'
import '@styles/react/libs/editor/editor.scss'
const SidebarNewUsers = ({ open, toggleSidebarTerms }) => {
  // ** States

  const handleSidebarClosedTerms = () => {

  }
  const [value, setValue] = useState(EditorState.createEmpty())
  return (

    <Sidebar
      size='lg'
      open={open}
      title='Edit Agency Worker Agreement'
      headerClassName='mb-1'
      contentClassName='pt-0'
      toggleSidebarTerms={toggleSidebarTerms}
      onClosed={handleSidebarClosedTerms}
    > 


          <Alert color='primary'>
            <div className='alert-body'>
              <Info size={20} />
              <span className='ms-1'>
              You can copy and paste your own agreement details here and save. Staff Agreement can be converted to PDF under staff profile management.
              </span>
            </div>
          </Alert>
          <div className='divider mt-2'>
            <div className='divider-text'>Edit agrement text</div>
          </div>
          <div className='mb-2'>
          <Label className='form-label' for='user-role'>
            Select User Type
          </Label>
          <Input type='select' id='user-role' name='user-type-agrement'>
            <option value='Healthcare Assistant'>Healthcare Assistant</option>
            <option value='Reagistered Nurse'>Reagistered Nurse</option>
            <option value='Doctor'>Doctor</option>
          </Input>
        </div>
          <Editor  editorState={value} onEditorStateChange={data => setValue(data)} />
          <Button type='submit' className='me-1' color='primary'>
          Submit
        </Button>
        <Button type='reset' color='secondary' outline onClick={toggleSidebarTerms}>
          Cancel
        </Button>


    </Sidebar>
  )
}

export default SidebarNewUsers
